    (airplane_capacity.max_capacity - flight.reserved_seats - IFNULL(flight_passenger_count.passenger_count,0)) as available_seats
    FROM
	flight
    INNER JOIN
    (SELECT
		airplane.id,
        airplane_type.max_capacity
